evarglice={doc:null,event_listen:null,bind:function(c,a,e){for(var b=0,d=a.length;b<d;b++){if(this.event_listen){a[b].addEventListener(c,e,false)}else{a[b].attachEvent("on"+c,e)}}return a},unbind:function(c,a,e){for(var b=0,d=a.length;b<d;b++){if(this.event_listen){a[b].removeEventListener(c,e,false)}else{a[b].detachEvent("on"+c,e)}}return a},init:function(){this.doc=document;this.event_listen=(this.doc.addEventListener?true:false);return this}}.init();